mapper.select:
SELECT p.id, p.name, p.category_id, p.price, p.original_price, p.stock, p.sales, p.cover_image, p.images, p.description, p.specs, p.status, p.is_hot, p.is_recommend, p.deleted, p.created_time, p.updated_time FROM `product` p LEFT JOIN `product_category` pc ON p.category_id = pc.id AND pc.deleted = 0 WHERE p.deleted = 0
AND p.category_id = #{categoryId}
AND (p.name LIKE CONCAT('%', #{keyword}, '%') OR p.description LIKE CONCAT('%', #{keyword}, '%'))
AND p.status = #{status}
ORDER BY p.created_time DESC

mapper.update:
UPDATE `product`
SET sales = sales + #{quantity}
WHERE id = #{productId}

UPDATE `product`
SET stock = stock + #{quantity}
WHERE id = #{productId}

UPDATE `product`
SET status = #{status}
WHERE id IN

    #{id}
